mapper.insert:
insert into TODO_LIST (USER_ID, TITLE, CATEGORY

    , MEMO
)
values (#{userId}, #{title}, #{category}

    , #{memo}
)

mapper.select:

    SELECT TODO_ID, USER_ID, TITLE, COMPLETED, CATEGORY, START_DATE, END_DATE
    FROM TODO_LIST
    WHERE START_DATE <= #{date}
      AND END_DATE >= #{date}

select TODO_ID, TITLE, MEMO, COMPLETED, CATEGORY, START_DATE, END_DATE
from TODO_LIST
where TODO_ID = #{todoId}
