mapper.select:
select user_id as userId, email, nickname as nickName, avatar_type as avatarType, password, lv, points, created_at as createdAt from users where user_id > 0
and email=#{email} and password=#{password}
and email=#{email}
and nickname = #{nickName}

mapper.update:
UPDATE Users
SET points = points + #{points}
WHERE user_id = #{userId}
